        /// <summary>
        /// 更加查询到的结果集，设置控制可用状态
        /// </summary>
        private void AfterQueControlsEnabled()
        {{
            if (dgvMain.DataGridView.Rows.Count > 0)
            {{
                btnList.Enabled = true;
                btnUpd.Enabled = true;
                btnDel.Enabled = true;
            }}
            else
            {{
                btnList.Enabled = false;
                btnUpd.Enabled = false;
                btnDel.Enabled = false;
            }}
        }}
        /// <summary>
        /// 根据指定环境标志重置控件状态
        /// </summary>
        /// <param name="iEnvironmentFlag"></param>
        private void RetControlsState(int iEnvironmentFlag)
        {{
            kmFPNLBtnGup.Visible = false;
            btnOk.Visible = false;
            btnCancel.Visible = false;
            //
            if (iEnvironmentFlag == 0)
            {{
                kmFPNLBtnGup.Visible = true;
                kmFPNLBtnGup.Enabled = true;
                btnCancel.Visible = true;
                btnCancel.Enabled = true;
            }}
            else if (iEnvironmentFlag == 1 || iEnvironmentFlag == 2 || iEnvironmentFlag == 3)
            {{
                btnOk.Visible = true;
                btnOk.Enabled = true;
                btnCancel.Visible = true;
                btnCancel.Enabled = true;
            }}
        }}

        /// <summary>
        /// 给WorkFiled填充数据，以便控件显示
        /// </summary>
        private void FillWorkFiledsValue(int iEnvironmentFlag)
        {{
            if (iEnvironmentFlag == 0)
            {{
                #region 0 细节
                ReFillWorkFiled();
                #endregion
            }}
            else if (iEnvironmentFlag == 1)
            {{
                #region 1 增加
                if (checkbAddDef.Checked)
                {{
                    ReFillWorkFiled();
                }}
                else
                {{
                    ksWorkView_ModDef.Reset(2);
                }}
                #endregion
            }}
            else if (iEnvironmentFlag == 2)
            {{
                #region 2 修改
                ReFillWorkFiled();
                #endregion
            }}
            else if (iEnvironmentFlag == 3)
            {{
                #region 3 删除
                ReFillWorkFiled();
                #endregion
            }}
        }}
        private void ReFillWorkFiled()
        {{
            DataGridViewRow dgvr = dgvMain.DataGridView.GetCurrentRow();
            if (dgvr == null)
            {{
                ksWorkView_ModDef.Reset(2);
            }}
            else
            {{
{2}
            }}
        }}
        #endregion

        private void btnList_Click(object sender, EventArgs e)
        {{
            if (null == dgvMain.DataGridView.CurrentRow)
            {{
                KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.SelectCurRowInGrid);
                return;
            }}
            //控件kmFPNLBtnGup使用前的初始化
            int iCurRowIndex = dgvMain.DataGridView.CurrentRow.Index + 1;
            int iMaxRowCount = dgvMain.DataGridView.Rows.Count;
            kmFPNLBtnGup.Init(iCurRowIndex, iMaxRowCount);
            PreEnvironmentFlag = 0;
            kpControlsVisible_ModDef.SetControlsVisible(PreEnvironmentFlag, true);
            kpControlsEnabled_ModDef.SetControlsEnabled(PreEnvironmentFlag, false);
            RetControlsState(PreEnvironmentFlag);
            FillWorkFiledsValue(PreEnvironmentFlag);
            SetCurTabPage(1, SysTMComm.Consts.CTipInfo.ModTabPageTitle_List);
        }}

        private void kmFPNLBtnGup_ClickBtnFirst(object sender, EventArgs e)
        {{
            if (dgvMain.DataGridView.Rows.Count > 0)
            {{
                if (dgvMain.DataGridView.CurrentRow.Index != 0)
                {{
                    dgvMain.DataGridView.SetCurrentRow(0);
                    FillWorkFiledsValue(PreEnvironmentFlag);
                }}
            }}
        }}
        private void kmFPNLBtnGup_ClickBtnNext(object sender, EventArgs e)
        {{
            if (dgvMain.DataGridView.CurrentRow.Index + 1 < dgvMain.DataGridView.Rows.Count)
            {{
                dgvMain.DataGridView.SetCurrentRow(dgvMain.DataGridView.CurrentRow.Index + 1);
                FillWorkFiledsValue(PreEnvironmentFlag);
            }}
        }}
        private void kmFPNLBtnGup_ClickBtnPrevious(object sender, EventArgs e)
        {{
            if (dgvMain.DataGridView.CurrentRow.Index > 0)
            {{
                dgvMain.DataGridView.SetCurrentRow(dgvMain.DataGridView.CurrentRow.Index - 1);
                FillWorkFiledsValue(PreEnvironmentFlag);
            }}
        }}
        private void kmFPNLBtnGup_ClickBtnLast(object sender, EventArgs e)
        {{
            if (dgvMain.DataGridView.Rows.Count > 0)
            {{
                dgvMain.DataGridView.SetCurrentRow(dgvMain.DataGridView.Rows.Count - 1);
                FillWorkFiledsValue(PreEnvironmentFlag);
            }}
        }}

        private void btnQue_Click(object sender, EventArgs e)
        {{
            try
            {{
                ksWorkViewCRequest_Que.Command = GolCmdNOSys.CmdAdapter;
                ksWorkViewCRequest_Que.ExecCommand();
            }}
            catch (KmNGTF.Common.Exceptions.NotQueryResult)
            {{
                dgvMain.DataGridView.Rows.Clear();
                if (PreEnvironmentFlag != 3)
                    KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.Que_NoRecordReturn);
            }}
            catch (Exception othErr)
            {{
                dgvMain.DataGridView.Rows.Clear();
                KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.Que_OprFail + othErr.Message);
            }}
            AfterQueControlsEnabled();
        }}
        private void btnAdd_Click(object sender, EventArgs e)
        {{
            PreEnvironmentFlag = 1;
            kpControlsVisible_ModDef.SetControlsVisible(PreEnvironmentFlag, true);
            kpControlsEnabled_ModDef.SetControlsEnabled(PreEnvironmentFlag, true);
            RetControlsState(PreEnvironmentFlag);
            FillWorkFiledsValue(PreEnvironmentFlag);
            SetCurTabPage(1, SysTMComm.Consts.CTipInfo.ModTabPageTitle_Add);
			this.btnOk.Enabled = this.ksWorkViewCRequest_Add.CheckValid();
        }}
        private void btnUpd_Click(object sender, EventArgs e)
        {{
            PreEnvironmentFlag = 2;
            kpControlsVisible_ModDef.SetControlsVisible(PreEnvironmentFlag, true);
            kpControlsEnabled_ModDef.SetControlsEnabled(PreEnvironmentFlag, true);
            RetControlsState(PreEnvironmentFlag);
            FillWorkFiledsValue(PreEnvironmentFlag);
            SetCurTabPage(1, SysTMComm.Consts.CTipInfo.ModTabPageTitle_Upd);
			this.btnOk.Enabled = this.ksWorkViewCRequest_Upd.CheckValid();
        }}
        private void btnDel_Click(object sender, EventArgs e)
        {{
            PreEnvironmentFlag = 3;
            kpControlsVisible_ModDef.SetControlsVisible(PreEnvironmentFlag, true);
            kpControlsEnabled_ModDef.SetControlsEnabled(PreEnvironmentFlag, false);
            RetControlsState(PreEnvironmentFlag);
            FillWorkFiledsValue(PreEnvironmentFlag);
            SetCurTabPage(1, SysTMComm.Consts.CTipInfo.ModTabPageTitle_Del);
        }}

        private void btnOk_Click(object sender, EventArgs e)
        {{
            if (PreEnvironmentFlag == 1)
            {{
                ksWorkViewCRequest_Add.Command = GolCmdNOSys.CmdAdapter;
                try
                {{
                    ksWorkViewCRequest_Add.ExecCommand();
                    KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.Add_OprSuccessful);
                }}
                catch (Exception curErr)
                {{
                    KmNGTF.Common.ShowMessageBox.ShowError(SysTMComm.Consts.CTipInfo.Add_OprFail + curErr.Message);
                    return;
                }}
            }}
            else if (PreEnvironmentFlag == 2)
            {{
                ksWorkViewCRequest_Upd.Command = GolCmdNOSys.CmdAdapter;
                try
                {{
                    ksWorkViewCRequest_Upd.ExecCommand();
                    KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.Upd_OprSuccessful);
                }}
                catch (Exception curErr)
                {{
                    KmNGTF.Common.ShowMessageBox.ShowError(SysTMComm.Consts.CTipInfo.Upd_OprFail + curErr.Message);
                    return;
                }}
            }}
            else if (PreEnvironmentFlag == 3)
            {{
                ksWorkViewCRequest_Del.Command = GolCmdNOSys.CmdAdapter;
                try
                {{
                    ksWorkViewCRequest_Del.ExecCommand();
                    KmNGTF.Common.ShowMessageBox.ShowInformation(SysTMComm.Consts.CTipInfo.Del_OprSuccessful);
                }}
                catch (Exception curErr)
                {{
                    KmNGTF.Common.ShowMessageBox.ShowError(SysTMComm.Consts.CTipInfo.Del_OprFail + curErr.Message);
                    return;
                }}
            }}

            SetCurTabPage(0, SysTMComm.Consts.CTipInfo.ModTabPageTitle_Que);
            btnQue_Click(null, null);
            AfterQueControlsEnabled();

            btnCancel_Click(null, null);
        }}
        private void btnCancel_Click(object sender, EventArgs e)
        {{
            PreEnvironmentFlag = -1;
            SetCurTabPage(0, SysTMComm.Consts.CTipInfo.ModTabPageTitle_Que);
        }}
        private void ksWorkView_ModDef_ValueChanged(object sender, EventArgs e)
        {{
            if (PreEnvironmentFlag == 1)
            {{
                this.btnOk.Enabled = this.ksWorkViewCRequest_Add.CheckValid();
            }}
            else if (PreEnvironmentFlag == 2)
            {{
                this.btnOk.Enabled = this.ksWorkViewCRequest_Upd.CheckValid();
            }}
        }}
    }}
